-- ----------------------------
-- Table structure for `es_message`
-- ----------------------------
DROP TABLE IF EXISTS `es_message`;
CREATE TABLE `es_message` (
  `id` int(8) NOT NULL AUTO_INCREMENT COMMENT '站内消息主键',
  `title` varchar(100) DEFAULT NULL COMMENT '标题',
  `content` longtext COMMENT '消息内容',
  `member_ids` longtext COMMENT '会员id',
  `admin_id` int(8) DEFAULT NULL COMMENT '管理员id',
  `admin_name` varchar(50) DEFAULT NULL COMMENT '管理员名称',
  `send_time` bigint(20) DEFAULT NULL COMMENT '发送时间',
  `send_type` int(2) DEFAULT NULL COMMENT '发送类型',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='站内消息(es_message)';

-- ----------------------------
-- Records of es_message
-- ----------------------------

-- ----------------------------
-- Table structure for `es_message_template`
-- ----------------------------
DROP TABLE IF EXISTS `es_message_template`;
CREATE TABLE `es_message_template` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '模版ID',
  `tpl_code` varchar(255) DEFAULT NULL COMMENT '模版编号',
  `tpl_name` varchar(255) DEFAULT NULL COMMENT '模板名称',
  `type` varchar(10) DEFAULT NULL COMMENT '类型(会员 ,店铺 ,其他)',
  `notice_state` varchar(10) DEFAULT '1' COMMENT '站内信提醒是否开启',
  `sms_state` varchar(10) DEFAULT '1' COMMENT '短信提醒是否开启',
  `email_state` varchar(10) DEFAULT '1' COMMENT '邮件提醒是否开启',
  `content` longtext COMMENT '站内信内容',
  `sms_content` longtext COMMENT '短信内容',
  `email_content` longtext COMMENT '邮件内容',
  `email_title` varchar(50) DEFAULT NULL COMMENT '邮件标题',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COMMENT='消息模版(es_message_template)';

